package practica2;

public class TablasMultiplicar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1; i <= 10; i++)
		{
			for(int x = 0; x <= 10; x++)
			{
				System.out.println(i + "x" + x + "=" + i * x);
			}
			System.out.println();
		}
	}
		
}

